
/// Operators in JS
/// Unary, Binary, and Ternary Operators

const sum = 5+8; // Binary operator (addition)
const difference = 10 - 3; // Binary operator (subtraction)
const product = 4 * 7; // Binary operator (multiplication)
const quotient = 20 / 4; // Binary operator (division)
const remainder = 10 % 3; // Binary operator (modulus)
const increment = 5; // Unary operator (increment)
const decrement = 10; // Unary operator (decrement)
const negation = -5;
console.log(negation); // Unary operator (negation)
const isTrue = true; // Boolean value
const isFalse = false; // Boolean value

const isEqual = (5 === 5); // Binary operator (equality)
const isNotEqual = (5 !== 3); // Binary operator (inequality)
const isGreater = (10 > 5);
const isLess = (3 < 7); // Binary operator (less than)
const isGreaterOrEqual = (10 >= 10);
const isLessOrEqual = (5 <= 10); // Binary operator (less than or equal to)
const logicalAnd = (true && false); // Binary operator (logical AND)
const logicalOr = (true || false); // Binary operator (logical OR)
const logicalNot = !true; // Unary operator (logical NOT)
const conditional = (5 > 3) ? "Greater" : "Less or Equal"; // Ternary operator (conditional)
console.log("Sum:", sum);
console.log("Difference:", difference);
console.log("Product:", product);
console.log("Quotient:", quotient);
console.log("Remainder:", remainder);
console.log("Increment:", increment);
console.log("Decrement:", decrement);
console.log("Negation:", negation);
console.log("Is True:", isTrue);
console.log("Is False:", isFalse);
console.log("Is Equal:", isEqual);
console.log("Is Not Equal:", isNotEqual);
console.log("Is Greater:", isGreater);
console.log("Is Less:", isLess);
console.log("Is Greater or Equal:", isGreaterOrEqual);
console.log("Is Less or Equal:", isLessOrEqual);
console.log("Logical And:", logicalAnd);
console.log("Logical Or:", logicalOr);
console.log("Logical Not:", logicalNot);
console.log("Conditional:", conditional);